mapper.delete:
DELETE
FROM d_image
WHERE select_type = #{select_type} AND primary_key = #{primary_key}

mapper.select:
SELECT hotel_tag_num
FROM d_hotel_reference_tag
WHERE hotel_num = #{hotel_num}

mapper.update:
UPDATE d_reservation SET reservation_status = 3 WHERE room_detail_num IN
#{room_detail_num}
AND reservation_status = 1

mapper.select:
SELECT re.reservation_num, re.resrvation_name AS customer_name, re.reservation_phone AS customer_phone_num, re.reservation_status, re.st_date, re.ed_date, r.name AS room_name, h.name AS hotel_name FROM d_reservation re LEFT JOIN d_room_detail rd ON rd.room_detail_num = re.room_detail_num LEFT JOIN d_room r ON rd.room_num = r.room_num LEFT JOIN d_hotel h on r.hotel_num = h.hotel_num WHERE re.room_detail_num IN
#{room_detail_num}
AND date_format(#{reservation_date}, '%Y-%m-%d') BETWEEN date_format(re.st_date,'%Y-%m-%d') AND date_format(re.ed_date, '%Y-%m-%d')
AND re.resrvation_name = #{customer_name}
AND re.reservation_phone = #{customer_phone_num}
AND reservation_num = #{reservation_num}
AND re.reservation_status = 1
AND (re.reservation_status = 2 OR re.reservation_status = 3)
AND re.reservation_status = 4
ORDER BY re.reservation_num DESC LIMIT #{page_cnt} OFFSET #{page}

SELECT count(*) FROM d_reservation re LEFT JOIN d_room_detail rd ON rd.room_detail_num = re.room_detail_num LEFT JOIN d_room r ON rd.room_num = r.room_num LEFT JOIN d_hotel h on r.hotel_num = h.hotel_num WHERE re.room_detail_num IN
#{room_detail_num}
AND date_format(#{reservation_date}, '%Y-%m-%d') BETWEEN date_format(re.st_date,'%Y-%m-%d') AND date_format(re.ed_date, '%Y-%m-%d')
AND re.resrvation_name = #{customer_name}
AND re.reservation_phone = #{customer_phone_num}
AND reservation_num = #{reservation_num}
AND re.reservation_status = 1
AND (re.reservation_status = 2 OR re.reservation_status = 3)
AND re.reservation_status = 4

SELECT bucket_url
FROM d_image
WHERE select_type = #{select_type}
  AND primary_key = #{primary_key}

mapper.update:
