     #{pk}

mapper.select:
SELECT rv.ed_date FROM d_room_detail rd LEFT JOIN d_reservation rv ON rd.room_detail_num = rv.room_detail_num WHERE rd.room_detail_num IN
#{room_detail_num}
AND rd.is_delete = 'N' AND rv.reservation_status = 1 ORDER BY rv.reservation_num DESC limit 1

SELECT name
FROM d_room
WHERE name = #{name}
AND room_num = #{room_num}

mapper.update:
UPDATE d_hotel
SET holiday_price_status = #{holiday_price_status},
    dt_update = NOW(),
    update_user = #{update_user}
WHERE hotel_num = #{hotel_num}

UPDATE d_room_detail
SET room_detail_status = 2,
    room_closed_start = #{room_closed_start},
    room_closed_end = #{room_closed_end}
WHERE room_detail_num = #{room_detail_num}

