mapper.insert:
insert into
d_reservation
(
member_num, room_detail_num, reservation_phone,
reservation_name, reservation_people, reservation_price, st_date, ed_date
,reservation_status, is_delete, dt_insert, insert_user
)
values(
#{member_num}, #{room_detail_num}, #{reservation_phone},
#{reservation_name},#{reservation_people}, #{reservation_price},
#{st_date}, #{ed_date}
,1 , 'N', NOW(), #{insert_user}
)

mapper.select:
select a.reservation_num, a.member_num, a.room_detail_num, a.reservation_phone, a.reservation_name, a.reservation_people, a.reservation_price, a.reservation_status, a.st_date, a.ed_date, d.name from d_reservation a inner join d_room_detail b ON a.room_detail_num = b.room_detail_num inner join d_room c ON c.room_num = b.room_num inner join d_hotel d ON c.hotel_num = d.hotel_num
where member_num = #{member_num} and reservation_status = #{reservation_status}
and st_date LIKE CONCAT(#{st_date},'%')
and ed_date LIKE CONCAT(#{ed_date},'%') ORDER BY reservation_num DESC LIMIT #{page_cnt} OFFSET #{page};


select 
	reservation_num, member_num, room_detail_num,
	reservation_phone, reservation_name,reservation_people,
	reservation_price, reservation_status, st_date, ed_date

from d_reservation

where reservation_name = #{reservation_name} 

	and reservation_phone = #{reservation_phone}

	and reservation_num = #{reservation_num}
